mapper.select:
        SELECT


--         级联表的字段
        ,wenzhang.zimeitiren_id as wenzhangZimeitirenId
        ,wenzhang.wenzhang_uuid_number as wenzhangUuidNumber
        ,wenzhang.wenzhang_name as wenzhangName
        ,wenzhang.wenzhang_photo as wenzhangPhoto
        ,wenzhang.wenzhang_types as wenzhangTypes
        ,wenzhang.wenzhang_erji_types as wenzhangErjiTypes
        ,wenzhang.wenzhang_clicknum as wenzhangClicknum
        ,wenzhang.zan_number as zanNumber
        ,wenzhang.cai_number as caiNumber
        ,wenzhang.wenzhang_content as wenzhangContent
        ,wenzhang.shangxia_types as shangxiaTypes
        ,wenzhang.wenzhang_delete as wenzhangDelete
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.shiyong_types as shiyongTypes
        ,yonghu.yonghu_email as yonghuEmail

        FROM wenzhang_collection  a
        left JOIN wenzhang wenzhang ON a.wenzhang_id = wenzhang.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.wenzhang_id = #{params.wenzhangId}
                )


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and a.wenzhang_collection_types = #{params.wenzhangCollectionTypes}


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




                and a.wenzhang_id IS NOT NULL


                and wenzhang.zimeitiren_id = #{params.zimeitirenId}


                and wenzhang.wenzhang_uuid_number like CONCAT('%',#{params.wenzhangUuidNumber},'%')


                and wenzhang.wenzhang_name like CONCAT('%',#{params.wenzhangName},'%')


                and wenzhang.wenzhang_types = #{params.wenzhangTypes}



                and wenzhang.wenzhang_erji_types = #{params.wenzhangErjiTypes}



                  and wenzhang.wenzhang_clicknum >= #{params.wenzhangClicknumStart}   


                  and wenzhang.wenzhang_clicknum <= #{params.wenzhangClicknumEnd}   


                and wenzhang.wenzhang_clicknum = #{params.wenzhangClicknum}


                  and wenzhang.zan_number >= #{params.zanNumberStart}   


                  and wenzhang.zan_number <= #{params.zanNumberEnd}   


                and wenzhang.zan_number = #{params.zanNumber}


                  and wenzhang.cai_number >= #{params.caiNumberStart}   


                  and wenzhang.cai_number <= #{params.caiNumberEnd}   


                and wenzhang.cai_number = #{params.caiNumber}


                and wenzhang.wenzhang_content like CONCAT('%',#{params.wenzhangContent},'%')


                and wenzhang.shangxia_types = #{params.shangxiaTypes}



                  and wenzhang.wenzhang_delete >= #{params.wenzhangDeleteStart}   


                  and wenzhang.wenzhang_delete <= #{params.wenzhangDeleteEnd}   


                and wenzhang.wenzhang_delete = #{params.wenzhangDelete}



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.shiyong_types = #{params.shiyongTypes}



                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')



        order by a.${params.orderBy} desc
